mapper.insert:
insert into cashbook values (CASHBOOK_SEQ.nextval, (select member_no from member where member_id=#{memberId}), #{cashbookFinance},#{cashbookDate}, #{cashbookLoop}, #{loopMonth}, #{cashbookAsset}, #{cashbookCategory}, #{cashbookMoney}, #{cashbookContent}, #{cashbookMemo}, #{challengeNo}, #{loopRound},
0
#{loopRef}
CASHBOOK_SEQ.currval
)
select max(cashbook_no) from cashbook

mapper.select:

select #{month} as month,nvl(sum(cashbook_money),0) as money
from cashbook 
where extract(year from cashbook_date) = 2023 and extract(month from cashbook_date)=#{month} 
and member_no=(select member_no from member where member_id=#{memberId}) and cashbook_finance=#{type}
